mapper.select:
select
  id, parent_id, name, enname, url, description, created, updated
from oauth2.tb_permission


        and id = #{id}


        and parent_id = #{parentId}


        and name = #{name}


        and enname = #{enname}


        and url = #{url}


        and description = #{description}


        and created = #{created}


        and updated = #{updated}
